ymaps.ready(init);

var myMap;

var oil = [];
var coal = [];
var oilgas = [];
var gasgor = [];
var burcoal = [];
var placemarks = [oil, coal, oilgas,  gasgor, burcoal];

function init() {
    myMap = new ymaps.Map("map", {
        center: [41.20, 74.00],
        zoom: 6,
        type: 'yandex#hybrid'
    });
    myMap.behaviors.enable('scrollZoom');
    myMap.controls.add('zoomControl', {
        float: 'none',
        position: {
            right: 40,
            top: 5
        }
    });

    var data = loadGeoData();
    for (var i=0; i < data.length; i++) {
        var row_data = data[i];

        var type = row_data[0];        
		var x = Number(row_data[10]);
		var y = Number(row_data[9]);
		var text = row_data[0];
        var size = Number(row_data[7]) * 10;
        
        var placemark = new ymaps.Placemark([x, y], {
			content: text,
			balloonContent: text + " тут!Э"
		}, {
            iconLayout: 'default#image',
            iconImageHref: getIcon(type),
            iconImageSize: [size, size],
            iconImageOffset: [-size/2, -size/2],
            visible: false
        });
        
        if (type == 'Нефть') {
            oil.push(placemark);
        } else if (type == 'Каменный уголь') {
            coal.push(placemark);
        } else if (type == 'Нефть и газ') {
            oilgas.push(placemark);
        } else if (type == 'Газы горючие') {
            gasgor.push(placemark);
        } else if (type == 'Бурый уголь') {
        	burcoal.push(placemark);
        }
    }

    putPlacemarksOnMap();

    var oil_button = new ymaps.control.Button('Нефть');
    var coal_button = new ymaps.control.Button('Каменный уголь');
    var oilgas_button = new ymaps.control.Button('Нефть и газ');
    var gasgor_button = new ymaps.control.Button('Газы горючие');
    var burcoal_button = new ymaps.control.Button('Бурый уголь');


    var typeControls = new ymaps.control.Group({
            items: [oil_button, coal_button, oilgas_button,  gasgor_button, burcoal_button]
        }, {
            position: { left: 40 }
    });

    oil_button.events.add(['select'], function (e) {
        showGroup(oil);
    });
    oil_button.events.add(['deselect'], function (e) {
        hideGroup(oil);
    });
    coal_button.events.add(['select'], function (e) {
        showGroup(coal);
    });
    coal_button.events.add(['deselect'], function (e) {
        hideGroup(coal);
    });
    oilgas_button.events.add(['select'], function (e) {
        showGroup(oilgas);
    });
    oilgas_button.events.add(['deselect'], function (e) {
        hideGroup(oilgas);
    });
    gasgor_button.events.add(['select'], function (e) {
        showGroup(gasgor);
    });
    gasgor_button.events.add(['deselect'], function (e) {
        hideGroup(gasgor);
    });
    burcoal_button.events.add(['select'], function (e) {
        showGroup(burcoal);
    });
    burcoal_button.events.add(['deselect'], function (e) {
        hideGroup(burcoal);
    });


    myMap.controls.add(typeControls);
}


function showGroup(group) {
    myMap.setCenter([41.20, 74.00], 6, { duration: 100000 });
    for (var i = 0; i < group.length; i++) {
        group[i].options.set({ visible: true });
    }
}

function hideGroup(group) {
    for (var i = 0; i < group.length; i++) {
        group[i].options.set({ visible: false });
    }
}

function getIcon(type) {
        if (type == 'Нефть') {
            return 'oil.png';
        } else if (type == 'Каменный уголь') {
            return 'coal.jpg';
        } else if (type == 'Нефть и газ') {
            return 'oilgas.jpg';
        } else if (type == 'Газы горючие') {
            return 'gasgor.jpg';
        } else if (type == 'Бурый уголь') {
        	return 'burcoal.jpg'
        }
}

function putPlacemarksOnMap() {
    for (var i = 0; i < placemarks.length; i++) {
        for (var j = 0; j < placemarks[i].length; j++) {
            myMap.geoObjects.add(placemarks[i][j]);
        }        
    }
};

